mapper.select:
select count(1) as 'count',substation from mbp_sub_street_shops
where  modify_time >= #{startTime} and modify_time <= #{endTime}
group by substation

select count(1) as 'count',CUSTOMER_MANAGER as 'customerManager' from mbp_sub_street_shops
where  modify_time >= #{startTime} and modify_time <= #{endTime}
  and substation = #{substation}
group by CUSTOMER_MANAGER

select CUSTOMER_MANAGER as 'customerManager',count(1) as 'count',substation from mbp_sub_street_shops
where  modify_time >= #{startTime} and modify_time <= #{endTime}
group by CUSTOMER_MANAGER
